package net.dean.ljgm.logging;

// TODO: Auto-generated Javadoc
/**
 * This Enum represents the levels that a message can be logged at.
 */
public enum LoggingLevel {
	
	/** The message will be logged with an INFO level. */
	INFO,
	
	/** The message will be logged with a WARN level. */
	WARN,
	
	/** The message will be logged with an ERROR level. */
	ERROR,
	
	/** The message will be logged with a DEBUG level. */
	DEBUG;
}
